		//证件号码（必填）
		<ID_CODE>522226194809144209</ID_CODE>
		//请求标志 固定为0，可不用传此值
		<REQFLAG>0</REQFLAG>
		//民族  DD[NATIONALITY]
		<NATIONALITY>00</NATIONALITY>
		//性别  DD[SEX]
		<SEX>0</SEX>
