		<length>15</length>
		<survey>
			//调查表的属性
			<property>
				//调查表编码
				<SURVEY_SN>1</SURVEY_SN>
				<SURVEY_NAME>个人版经纪业务客户风险评估问卷</SURVEY_NAME>
