			</group>
			//评定级别
			<level>
				<ITEM id="1" ratingLvl="0" name="未评级" baseScore="0" />
				<ITEM id="1" ratingLvl="1" name="保守型" baseScore="15" />
				<ITEM id="1" ratingLvl="2" name="稳健型" baseScore="33" />
				<ITEM id="1" ratingLvl="3" name="积极型" baseScore="77" />
			</level>
