public String downloadImgString(String xml);
Ø	入参格式：
<?xml version="1.0" encoding="UTF-8"?>
<request>
	<auth>
		<OPERATOR>33146</OPERATOR>
	</auth>
	<data>
		//影像编号（必填）
		<IMG_SN>2013011000000095</IMG_SN>


        //以下参数为查询身份证头像时需要传递的参数
		//用户名称
		<USER_NAME>傅小司</USER_NAME>
		//证件号码（如果非空，会调用公安联网查询）
		<ID_CODE>522226194809144209</ID_CODE>
		//民族  DD[NATIONALITY]
		<NATIONALITY>00</NATIONALITY>
		//性别  DD[SEX]
		<SEX>0</SEX>
		//出生日期（必填）
		<BIRTHDAY>20040914</BIRTHDAY>

	</data>
</request>
Ø	Str格式出参说明
//从Str对象获取到byte[]，其中byte[]分为三个部分。
byte[] bs = str.getByte();
//第一部分：前四个字节转化成int型表示第二部分的长度
int strLen = byteArrayToInt(bs, 0);
//第二部分：返回的字符串格式见出参格式
result = new String(bs, 4, strLen);
System.out.println(result);
//第三部分：二进制流数据，图片的二进制流信息。
FileOutputStream fops = new FileOutputStream(new File("S:\\hello.jpg"));
fops.write(bs, strLen + 4, bs.length - strLen - 4);
fops.close();

public static int byteArrayToInt(byte[] b, int offset) {
	int value = 0;
	for (int i = 0; i < 4; i++) {
		int shift = (4 - 1 - i) * 8;
		value += (b[i + offset] & 0x000000FF) << shift;// 往高位游
	}
	return value;
}
Ø	出参格式：
<?xml version="1.0" encoding="UTF-8"?>
<response>
	<result>
		<flag>1</flag>
		<prompt>successful</prompt>
		<length>1</length>
	</result>
	<record>
		<row>
			//总数据大小
			<TOTAL_DATA_SIZE>49096</TOTAL_DATA_SIZE>
			//影像数据
			<IMG_DATA></IMG_DATA>
			//数据长度
			<DATA_LEN>_BLOB_2_2</DATA_LEN>
			//影像类型
			<IMG_FMT>49096</IMG_FMT>
			//影像类型
			<IMG_FMT1>1</IMG_FMT1>
			//影像类别
			<IMG_CLS>1</IMG_CLS>
			//影像类别
			<IMG_CLS1>021</IMG_CLS1>
			//影像扩展名
			<IMG_TYPE>.JPG</IMG_TYPE>
		</row>
	</record>
</response>
