	<result>
		<flag>1</flag>
		<prompt>successful</prompt>
		<length>1</length>
	</result>
	<record>
		<VALIDATE_CODE>572583</VALIDATE_CODE>
		<SMS_TEXT>您好！本次非现场开户系统登录校验码：572583。详询4008001001</SMS_TEXT>
	</record>
</response>
