        //证件类型（必传）
		< ID_TYPE ></ ID_TYPE >
        //证件号码（必传）
		< ID_CODE ></ ID_CODE >
        //机构编码（必传）
		< INT_ORG ></ INT_ORG >
        //客户代码（必传）
		< CUST_CODE ></ CUST_CODE >
		//出生日期/注册日期（必传）
		< BIRTHDAY ></ BIRTHDAY >
        //证件开始日期（必传）
