2.104	风险测评结果查询
Ø	函数原型
public String queryRiskSurveyResult (String xml)
Ø	入参格式
<?xml version="1.0" encoding="UTF-8"?>
<request>
	<auth>
		<OPERATOR>36050</OPERATOR>
	</auth>
	<data>
		//调查表编码（必传）（默认为1）
		<SURVEY_SN></SURVEY_SN>
		//用户代码（必传）
		< USER_CODE></USER_CODE>
		//用户角色（必传）
		<USER_ROLE ></USER_ROLE>
		//当需要查询某个时间段内，客户做的所有的风险测评时，送开始日期和结束日期。
		若取最新一条则无需送开始日期和结束日期。
		//开始日期（非必传）
		<BGN_DATE></BGN_DATE>
		//结束日期（非必传）
		<END_DATE></END_DATE>
		//版本（非必传）
		<VERSION></VERSION>
	</data>
</request>

Ø	出参格式
<?xml version="1.0" encoding="UTF-8"?>
<response>
	<result>
        <flag>1</flag> 
		<prompt>successful</prompt>//  操作成功/失败的结果信息
        <length>1</length>
	</result>
	<record>
	    <row>
			//调查表编码
			<SURVEY_SN></ SURVEY_SN>
			//用户代码
			<USER_CODE></USER_CODE>
//用户角色
			<USER_ROLE></ USER_ROLE>
			//调查表分值
			<SURVEY_SCORE></SURVEY_SCORE>
//评级级别
			<RATING_LVL></ RATING_LVL>
			//评级级别名称
			<RATING_LVL_NAME></RATING_LVL_NAME>
//评级日期
			<RATING_DATE></ RATING_DATE>
			//调查表分值
			<SURVEY_SCORE></SURVEY_SCORE>
//有效截止日期
			<EXP_DATE></ EXP_DATE>
			//调查表类别
			<SURVEY_CLS></SURVEY_CLS>
//调查表名称
			<SURVEY_NAME></ SURVEY_NAME>
			//远程同步系统
			<SURVEY_SYN></SURVEY_SYN>
			//次序
			<ORDINAL></ORDINAL>
			//版本
			<VERSION></VERSION>
			//下次可测评日期
			<NEXT_RATING_DATE></NEXT_RATING_DATE>
			//试题串
			<SURVEY_COLS></SURVEY_COLS>
			//答案串
			<SURVEY_CELLS></SURVEY_CELLS>
</row>
	</record>
</response>

