<?xml version="1.0" encoding="UTF-8"?>
<response>
	<result>
        <flag>1</flag>// 1=成功  0=失败
		<prompt>successful</prompt>//  操作成功/失败的结果信息
        <length>1</length>
	</result>
	<record>
