			//交易单元
			< STKPBU ></STKPBU>	
			//指定结算参与人
			< FIRMID ></FIRMID>	
			//账户业务代理网点
			< ACCTBIZ_AGTCODE ></ACCTBIZ_AGTCODE>	
			//账户代理业务网点名称
			< ACCTBIZ_AGTNAME ></ACCTBIZ_AGTNAME>	
			//账户业务代理机构代码
			< ACCTBIZ_ORGCODE ></ACCTBIZ_ORGCODE>	
			//账户业务代理机构名称
			< ACCTBIZ_ORGNAME ></ACCTBIZ_ORGNAME>	
