			< TRDACCT >< / TRDACCT >
//交易市场
			< STKEX >< / STKEX >
//板块
			< STKBD >< / STKBD >
//权限类型 0-允许，1-禁止
			< GRANT_TYPE >< / GRANT_TYPE >
//交易行为 @-全部
			< TRADES >< / TRADES >
//证券类别
			< SECU_CLS2 >< / SECU_CLS2>
//证券内码
			< SECU_INTL >< / SECU_INTL >
</row>
	</record>
</response>

2.193	券商发起同步利息归本
Ø	函数原型
public String cubsbScSyncInterest (String xml)
Ø	入参格式
<?xml version="1.0" encoding="UTF-8"?>
<request>
	<auth>
		<OPERATOR>36050</OPERATOR>
	</auth>
	<data>
//客户代码（非必传）
        < CUST_CODE > </CUST_CODE >
//资产账户（必传）
        < CUACCT_CODE > </ CUACCT_CODE >
//币种（必传）
        < CURRENCY >0</ CURRENCY >
//外部机构（非必传）
        < EXT_ORG ></ EXT_ORG >
//备注（非必传）
        < OP_REMARK ></ OP_REMARK >
	</data>
</request>

Ø	出参格式
