        <flag>1</flag>// 1=成功  0=失败
		<prompt>successful</prompt>//  操作成功/失败的结果信息
        <length>1</length>
	</result>
	<record>
//流水序号
        < SERIAL_NO > </ SERIAL_NO >
	</record>
</response>
